example.code:
var setBigClass = function( img ) {
  if ( $( img ).height() > 100 ) {
    $( img ).addClass( "bigImg" );
  }
};

$( "img.userIcon" ).each(function() {
  var src = this.src;
  var blank = "data:image/gif;base64,R0lGODlhAQABAIAAAAAAAP///ywAAAAAAQABAAACAUwAOw==";

  // IE sets a "complete" property on already-loaded images
  if ( this.complete ) {
    setBigClass( this );
  } else {
    $( this ).load( setBigClass );

    // Avoid Webkit bug by quickly swapping src back and forth
    this.src = blank;
    this.src = src;
  }
});
